package com.mycelium.nbt.model.entities;import java.util.ArrayList;import java.util.List;import java.util.Date;public class TaskEntity extends Entity {	private String _id;	private String _caption;	private String  _description;	private List<String> _attachedCRs;	private List<String> _attachedModules;	private Date _dateOfCreation;			public TaskEntity()	{		_caption="";		_description="";		_attachedCRs=new ArrayList<String>();		_attachedModules=new ArrayList<String>();		_dateOfCreation=new Date();		}	public TaskEntity(String caption,String  description,List<String> attachedCRs,List<String> attachedModules,Date dateOfCreation)	{		_caption=caption;		_description=description;		_attachedCRs=attachedCRs;		_attachedModules=attachedModules;		_dateOfCreation=dateOfCreation;		}	@Override	public String getId() {		return _id;	}	public void setId(String id) {		_id = id;	}	public String getCaption() {		return _caption;	}	public String getDescription() {		return _description;	}	public void setDescription(String description) {		_description=description;	}		public void setCaption(String caption) {		_caption = caption;	}		public List<String> getAttachedCRs() {		return _attachedCRs;	}	public void setAttachedCRs(List<String> attachedCRs) {		_attachedCRs = attachedCRs;	}		public List<String> getAttachedModules() {		return _attachedModules;	}	public void setAttachedModules(List<String> attachedModules) {		_attachedModules = attachedModules;	}		public Date getDateOfCreation() {		return _dateOfCreation;	}	public void setDateOfCreation(Date dateOfCreation) {		_dateOfCreation = dateOfCreation;	}}